mapper.update:
update g_fund_history
set notify_status = 1, status = 1 where wx_trade_no =
#{orderId,jdbcType=VARCHAR}

mapper.select:
select

from g_fund_history
where `uid` = #{uid,jdbcType=INTEGER}

	and id < #{id,jdbcType=INTEGER}

and `status` = 1 and (notify_status=1 or notify_status is null)
order by time desc limit 0,10
