mapper.select:
select count(*) from cloud_order where uid= #{uid,jdbcType=INTEGER}
and subject = #{subject, jdbcType=VARCHAR} and status = 0

and human=0

select a.id, a.`uid`,a.chnno, a.subject_id as subjectId, a.subject, a.direction, a.money, a.amount, a.contract_money as contractMoney, a.`limit`, a.cleared, a.order_time as orderTime, a.clear_time as clearTime, a.order_index as orderIndex, a.clear_index as clearIndex, a.commission, a.`status`, a.clear_upper_limit as clearUpperLimit, a.clear_down_limit as clearDownLimit,b.cname,a.coupon_commission as couponCommission,a.cash as cash ,a.sys_profit as sysProfit,a.sys_loss as sysLoss,a.coupou_money as coupouMoney from cloud_order a left join cloud_target b on a.subject = b.name where a.`uid` = #{uid,jdbcType=INTEGER}
and a.id < #{id,jdbcType=INTEGER}
and a.human=0 order by a.order_time desc limit 0,10

mapper.update:
update cloud_order set status = 1, clear_index =
#{index,jdbcType=INTEGER}
where  (clear_upper_limit <= #{index,jdbcType=INTEGER} or clear_down_limit >= #{index,jdbcType=INTEGER}) and subject = #{subject, jdbcType=VARCHAR} and status = 0

mapper.select:
select count(1)
from cloud_order
where 1=1
and `uid` =
#{uid,jdbcType=INTEGER}
and `subject` = #{subject,jdbcType=VARCHAR}
and
`status` = #{status,jdbcType=INTEGER}

and human=0

select count(*) as total_orders, sum(contract_money) as total_amount, sum(hidden) as hide_times from cloud_order
where  `uid` = #{uid,jdbcType=INTEGER} and `subject` = #{subject,jdbcType=VARCHAR}
and `order_time` >= #{startDate,jdbcType=VARCHAR} and `order_time` < #{endDate,jdbcType=VARCHAR}
